import React, { useState } from 'react';
import Axios from "axios";
import _ from "lodash";
import moment from "moment";

const App = () => {
  const [state , setState] = useState({});
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const [cityName, setCityName] = useState("");
  const [apiKey,setApiKey] = useState("");

  React.useEffect(() => {
    // fetchData();
  }, []);

  const fetchData = () => {
    setError("");
    ;
    setLoading(true);
    Axios.get(`http://api.shipping.esoftplay.com/waybill/${cityName}&appid=${apiKey}&units=metric`)
      .then(res => {
        setState(res.data);
        setLoading(false);
      }).catch(err => {
        setError("Unable to get weather information");
        console.log(err)
        setLoading(false);
      });

    // Axios.post("url", data, {headers: {
    //     "Content-Type": "multipart/form-data",
    //     "Content-Type": "application/json",
    //   }})
  };

  const displayInformation = () => {
    if (error) {
      return <p className={"danger"}>{error}</p>
    }

    if (_.isEmpty(state) && loading) {
      return <p>Loading weather information</p>
    }

    if (_.isEmpty(state)) {
      return false
    }

    return (
      <>
        <p>The temperature is currently {state.manifest.manifest_description}</p>
        

        <div className={"divider"}/>
      </>
    )
  };

  return (
    <div className="cont">
      <h1 className={"title"}>Cek Resi</h1>

      <div>
        {displayInformation()}
      </div>

      <p>Enter resi</p>
      <input onChange={event => setCityName(event.target.value)} className={"input"}/>
      
      <input onChange={event => setApiKey(event.target.value)} className={"input"}/>
      <button className={"button"} onClick={fetchData}>Get Weather</button>
    </div>
  );
}

export default App;
